import React from "react";
import { connect } from "react-redux";
import {
  Form,
  Button,
  Message,
  Grid,
  Header,
  Segment
} from "semantic-ui-react";
import { login, loginGoogle } from "../../services/redux/actions/authActions";
import withSemanticUIFormik from "../../components/hoc/FormikSUI";
import * as Yup from "yup";
import { Redirect } from "react-router-dom";
import Google from "./social/Google";

const MyInnerForm = props => {
  const {
    errors,
    isSubmitting,
    handleChange,
    handleSubmit,
    userHasLoggedin,
    loginHasFailed,
    loginGoogleRequest
  } = props;

  if (userHasLoggedin) {
    return (
      <Redirect
        to={{
          pathname: "/dashboard/budgets",
          state: {
            from: props.location
          }
        }}
      />
    );
  } else {
    return (
      <Grid
        style={{
          height: "100%"
        }}
        verticalAlign="middle"
        centered
      >
        <Grid.Row centered>
          <Grid.Column width={6} style={{ paddingTop: "2em", maxWidth: 450 }}>
            <Header as="h2" color="black" textAlign="center">
              Ingresa con tu cuenta
            </Header>
            <Segment stacked>
              {console.log(errors)}
              {Object.keys(errors).length > 0 ? (
                <Message
                  error
                  header="Campos invalidos"
                  list={Object.keys(errors).map(key => errors[key])}
                />
              ) : loginHasFailed ? (
                <Message
                  error
                  header="Hay problemas con el inicio de sesion"
                  content={
                    "err" in loginHasFailed ? (
                      <p>{loginHasFailed.err.message}</p>
                    ) : "errors" in loginHasFailed ? (
                      <p>{JSON.stringify(loginHasFailed, null, 4)}</p>
                    ) : null
                  }
                />
              ) : null}
              <Form size="large" onSubmit={handleSubmit} loading={isSubmitting}>
                <Form.Input
                  label="Email electrónico"
                  labelPosition="left"
                  fluid
                  icon="user"
                  type="email"
                  name="email"
                  iconPosition="left"
                  placeholder="Email electrónico..."
                  onChange={handleChange}
                />
                <Form.Input
                  label="Contraseña"
                  labelPosition="left"
                  type="password"
                  fluid
                  icon="lock"
                  iconPosition="left"
                  name="password"
                  placeholder="Contraseña..."
                  onChange={handleChange}
                />
                <Button
                  id="submit-login"
                  type="submit"
                  style={{ width: "100%", height: "auto" }}
                >
                  Iniciar Sesión
                </Button>
              </Form>
              <Header as="h2" color="black" textAlign="center">
                O CON{" "}
              </Header>
              <Grid.Row>
                <Grid.Column width={3}>
                  <Google onLoginSuccess={loginGoogleRequest} />
                </Grid.Column>
              </Grid.Row>
            </Segment>
          </Grid.Column>
        </Grid.Row>
      </Grid>
    );
  }
};

const mapState = state => {
  return {
    userHasLoggedin: state.authService.loginSuccess,
    loginHasFailed: state.authService.loginFailure
  };
};

const mapDispatch = dispatch => {
  return {
    loginRequest: user => {
      dispatch(login(user));
    },
    loginGoogleRequest: user => {
      dispatch(loginGoogle(user));
    }
  };
};

export default connect(
  mapState,
  mapDispatch
)(
  withSemanticUIFormik({
    mapPropsToValues: () => ({ email: "", password: "" }),
    validationSchema: Yup.object().shape({
      email: Yup.string()
        .email("Email invalido")
        .required("Email es requerido!"),
      password: Yup.string().required("Constraseña requerida!")
    }),
    handleSubmit: (values, { setSubmitting, props }) => {
      setTimeout(() => {
        props.loginRequest(values);
        setSubmitting(false);
      }, 1000);
    },
    displayName: "LoginForm"
  })(MyInnerForm)
);
