import React from "react";
import ReactTable from "react-table";
import "react-table/react-table.css";
import { Button, Icon } from "semantic-ui-react";
import "./styles/data.table.css";
const ActionsCell = ({
  handleOnUpdate,
  handleOnDelete,
  handleOnView,
  original
}) => (
  <Button.Group>
    {handleOnView && (
      <Button icon size="tiny" onClick={handleOnView(original)}>
        <Icon name="eye" />
      </Button>
    )}
    {handleOnUpdate && (
      <Button icon size="tiny">
        <Icon name="edit" onClick={handleOnUpdate(original)} />
      </Button>
    )}{" "}
    {handleOnDelete && (
      <Button icon size="tiny">
        <Icon name="trash" onClick={handleOnDelete(original)} />
      </Button>
    )}
  </Button.Group>
);

export default class DataTable extends React.PureComponent {
  getColumns = data => {
    const columns = [];
    const sample = data[0];
    const { handlers, actions } = this.props;
    Object.keys(sample).forEach(key => {
      if (key !== "_id" && key !== "id") {
        if (sample[key] && typeof sample[key] !== "object") {
          let column = {
            accessor: key,
            Header: key,
            className: "center"
          };
          columns.push(column);
        }
      }
    });
    if (actions) {
      columns.push({
        Header: "",
        Cell: cellInfo => <ActionsCell {...handlers} {...cellInfo} />
      });
    }
    return columns;
  };

  render() {
    const { data } = this.props;
    if (!data) return null;
    const columns = this.getColumns(data);
    return (
      <div>
        <ReactTable
          data={data}
          columns={columns}
          defaultPageSize={10}
          className="-striped -highlight"
        />
      </div>
    );
  }
}
